import React, { useState } from "react";
import "./Pageone.css";
import { countries } from "../countries";
import { useDispatch } from 'react-redux';
import { updateFormData } from '../store'; // Ensure this path is correct based on your project structure

const Page1 = ({ onNext }) => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    applicationDate: "",
    country: "",
  });

  const dispatch = useDispatch();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Dispatch the form data update action here, using the current form data state
    dispatch(updateFormData(formData));
    onNext(); // Navigate to the next page
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Form fields */}
      <button type="submit">Save and Proceed</button>
    </form>
  );
};

export default Page1;
