import React, { useState } from 'react';

const PaginationComponent = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = 3;

  const goToNextPage = () => {
    setCurrentPage((prevPage) => (prevPage < totalPages ? prevPage + 1 : prevPage));
  };

  const goToPreviousPage = () => {
    setCurrentPage((prevPage) => (prevPage > 1 ? prevPage - 1 : prevPage));
  };

  const renderPageContent = () => {
    switch (currentPage) {
      case 1:
        return <div>Page 1 Content</div>;
      case 2:
        return <div>Page 2 Content</div>;
      case 3:
        return <div>Page 3 Content</div>;
      default:
        return <div>Page Not Found</div>;
    }
  };

  return (
    <div>
      {renderPageContent()}
      <div style={{ marginTop: '20px' }}>
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>&larr; Prev</button>
        <button onClick={goToNextPage} disabled={currentPage === totalPages} style={{ marginLeft: '10px' }}>Next &rarr;</button>
      </div>
    </div>
  );
};

export default PaginationComponent;
