import React, { useState } from "react";
import "./Pageone.css";
import { countries } from "../countries";
// In your Page1 and Page2 components
import { useDispatch } from 'react-redux';
import { updateFormData } from './store';

const dispatch = useDispatch();

// When updating form data
dispatch(updateFormData({ firstName: 'John', lastName: 'Doe' }));


const Page1 = ({ onNext, onFormDataChange  }) => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    applicationDate: "",
    country: "",
  });


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => {
      const updatedFormData = { ...prevState, [name]: value };
      // Assuming this function is provided to update the parent component's state
      onFormDataChange(updatedFormData); 
      return updatedFormData;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // Process form data here or pass it to parent component
    onNext(); // Assuming you have a method to go to the next page
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>First Name:</label>
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Last Name:</label>
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Application Date:</label>
        <input
          type="date"
          name="applicationDate"
          value={formData.applicationDate}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Country:</label>
        <select
          name="country"
          value={formData.country}
          onChange={handleChange}
          required
        >
          <option value="">Select a country</option>
          {countries.map((country) => (
            <option key={country.code} value={country.code}>
              {country.name}
            </option>
          ))}
        </select>
      </div>
      <button onClick={onNext}>Save and Proceed</button>
    </form>
  );
};

export default Page1;
