import React, { useState } from 'react';
import Page1 from './Pages/pageOne/Page1'
import Page2 from './Pages/Page2';
import Page3 from './Pages/Page3';

const PaginationComponent = () => {
  const [currentPage, setCurrentPage] = useState(1);

  // Import Page1, Page2, and Page3 at the top

const goToNextPage = () => {
  setCurrentPage((currentPage) => currentPage + 1);
};

// When rendering Page1, pass goToNextPage as a prop
// Example:
{currentPage === 1 && <Page1 onNext={goToNextPage} />}


  const goToPreviousPage = () => {
    setCurrentPage(currentPage - 1);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 1:
        return <Page1 />;
      case 2:
        return <Page2 />;
      case 3:
        return <Page3 />;
      default:
        return <Page1 />;
    }
  };

  return (
    <div>
      {renderPage()}
      <div>
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>&larr; Prev</button>
        <button onClick={goToNextPage} disabled={currentPage === 3} style={{ marginLeft: '10px' }}>Next &rarr;</button>
      </div>
    </div>
  );
};

export default PaginationComponent;
