import React, { useState } from 'react';
import countries from './countries.json'; // Adjust path as necessary

const Page2 = ({ onNext, formData }) => {
  const [allChecked, setAllChecked] = useState(false);

  return (
    <div>
      <p>Hi {formData.firstName} {formData.lastName}</p>
      <table>
        <tbody>
          {countries.map((country, index) => (
            <tr key={index}>
              <td>
                <input type="checkbox" id={`country-${index}`} name="countries" value={country.code} />
                <label htmlFor={`country-${index}`}>{country.name}</label>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <input type="radio" id="confirm" name="confirmation" onChange={() => setAllChecked(true)} />
        <label htmlFor="confirm">I have checked all the details</label>
      </div>
      <button onClick={onNext} disabled={!allChecked}>Save and Proceed</button>
    </div>
  );
};

export default Page2;
